    <script>
        // Mendefinisikan fungsi functionReveal()
        function functionReveal(event) {
            event.preventDefault(); // mencegah pengalihan halaman
            // Menambahkan elemen input textbox pada masing-masing div
            const loginBox = document.getElementById("textboxLogin");
            const passwordBox = document.getElementById("textboxPassword");
            loginBox.innerHTML =
                '<div class="form-outline mb-4"><input type="text" id="email" name="email" class="form-control rounded" placeholder="Enter email"></div>';
            passwordBox.innerHTML =
                ' <div class="form-outline mb-3"><input type="password" id="password" name="password" class="form-control rounded" placeholder="Password"></div>';

            // Mengganti nama fungsi saat tombol login ditekan
            const loginButton = document.getElementById("submit_login_customer");
            loginButton.setAttribute("onclick", "siapSubmit()");
        }

        function functionReveal2(event) {
            event.preventDefault(); // mencegah pengalihan halaman
            // Menambahkan elemen input textbox pada masing-masing div
            const loginBox = document.getElementById("textboxLogin2");
            const passwordBox = document.getElementById("textboxPassword2");
            loginBox.innerHTML =
                '<div class="form-outline mb-4"><input type="text" id="email" name="email" class="form-control" placeholder="Enter email"></div>';
            passwordBox.innerHTML =
                ' <div class="form-outline mb-3"><input type="password" id="password" name="password" class="form-control" placeholder="Password"></div>';

            // Mengganti nama fungsi saat tombol login ditekan
            const loginButton = document.getElementById("submit_login_customer2");
            loginButton.setAttribute("onclick", "siapSubmit()");
        }

        // Mendefinisikan fungsi siapSubmit()
        function siapSubmit() {
            window.location.href = response.data.redirect;
        }
    </script>




